import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav style={{ padding: '1rem', backgroundColor: '#f0f0f0', marginBottom: '2rem' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Link to="/" style={{ fontSize: '1.5rem', fontWeight: 'bold', textDecoration: 'none' }}>
          Travel Planner
        </Link>
        <div>
          {user ? (
            <>
              <Link to="/dashboard" style={{ marginRight: '1rem', textDecoration: 'none' }}>
                Dashboard
              </Link>
              <Link to="/itineraries" style={{ marginRight: '1rem', textDecoration: 'none' }}>
                Itineraries
              </Link>
              <button onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" style={{ marginRight: '1rem', textDecoration: 'none' }}>
                Login
              </Link>
              <Link to="/register" style={{ textDecoration: 'none' }}>
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
