import { useState, useEffect } from 'react';
import { itineraryAPI } from '../services/api';
import GoogleMap from '../components/GoogleMap';

const Itineraries = () => {
  const [itineraries, setItineraries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    destinations: [{ location: '', startDate: '', endDate: '', notes: '', latitude: '', longitude: '' }]
  });

  useEffect(() => {
    fetchItineraries();
  }, []);

  const fetchItineraries = async () => {
    try {
      const response = await itineraryAPI.getAll();
      setItineraries(response.data);
    } catch (err) {
      setError('Failed to fetch itineraries');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await itineraryAPI.create(formData);
      setFormData({
        title: '',
        destinations: [{ location: '', startDate: '', endDate: '', notes: '', latitude: '', longitude: '' }]
      });
      setShowForm(false);
      fetchItineraries();
    } catch (err) {
      setError('Failed to create itinerary');
    }
  };

  const handleDelete = async (id) => {
    try {
      await itineraryAPI.delete(id);
      fetchItineraries();
    } catch (err) {
      setError('Failed to delete itinerary');
    }
  };

  const addDestination = () => {
    setFormData({
      ...formData,
      destinations: [...formData.destinations, { location: '', startDate: '', endDate: '', notes: '', latitude: '', longitude: '' }]
    });
  };

  const updateDestination = (index, field, value) => {
    const updated = formData.destinations.map((dest, i) => 
      i === index ? { ...dest, [field]: value } : dest
    );
    setFormData({ ...formData, destinations: updated });
  };

  const getAllDestinations = () => {
    return itineraries.flatMap(itinerary => 
      itinerary.destinations.filter(dest => dest.latitude && dest.longitude)
    );
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: '2rem' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h1>My Itineraries</h1>
        <button onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : 'Add New Itinerary'}
        </button>
      </div>

      {error && <div style={{ color: 'red', marginBottom: '1rem' }}>{error}</div>}

      {showForm && (
        <form onSubmit={handleSubmit} style={{ marginBottom: '2rem', padding: '1rem', border: '1px solid #ccc' }}>
          <div style={{ marginBottom: '1rem' }}>
            <input
              type="text"
              placeholder="Itinerary Title"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              required
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          {formData.destinations.map((dest, index) => (
            <div key={index} style={{ marginBottom: '1rem', padding: '1rem', backgroundColor: '#f9f9f9' }}>
              <h4>Destination {index + 1}</h4>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.5rem', marginBottom: '0.5rem' }}>
                <input
                  type="text"
                  placeholder="Location"
                  value={dest.location}
                  onChange={(e) => updateDestination(index, 'location', e.target.value)}
                  required
                />
                <input
                  type="date"
                  placeholder="Start Date"
                  value={dest.startDate}
                  onChange={(e) => updateDestination(index, 'startDate', e.target.value)}
                  required
                />
                <input
                  type="date"
                  placeholder="End Date"
                  value={dest.endDate}
                  onChange={(e) => updateDestination(index, 'endDate', e.target.value)}
                  required
                />
                <input
                  type="number"
                  step="any"
                  placeholder="Latitude (optional)"
                  value={dest.latitude}
                  onChange={(e) => updateDestination(index, 'latitude', e.target.value ? parseFloat(e.target.value) : '')}
                />
                <input
                  type="number"
                  step="any"
                  placeholder="Longitude (optional)"
                  value={dest.longitude}
                  onChange={(e) => updateDestination(index, 'longitude', e.target.value ? parseFloat(e.target.value) : '')}
                />
              </div>
              <textarea
                placeholder="Notes (optional)"
                value={dest.notes}
                onChange={(e) => updateDestination(index, 'notes', e.target.value)}
                style={{ width: '100%', padding: '0.5rem' }}
              />
            </div>
          ))}

          <div style={{ marginBottom: '1rem' }}>
            <button type="button" onClick={addDestination}>Add Another Destination</button>
          </div>

          <button type="submit">Create Itinerary</button>
        </form>
      )}

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>
        <div>
          <h2>Itineraries</h2>
          {itineraries.length === 0 ? (
            <p>No itineraries yet. Create your first one!</p>
          ) : (
            itineraries.map((itinerary) => (
              <div key={itinerary._id} style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <h3>{itinerary.title}</h3>
                  <button onClick={() => handleDelete(itinerary._id)} style={{ color: 'red' }}>
                    Delete
                  </button>
                </div>
                {itinerary.destinations.map((dest, index) => (
                  <div key={index} style={{ marginBottom: '0.5rem', paddingLeft: '1rem' }}>
                    <strong>{dest.location}</strong> - {dest.startDate} to {dest.endDate}
                    {dest.notes && <p style={{ margin: '0.25rem 0', fontStyle: 'italic' }}>{dest.notes}</p>}
                  </div>
                ))}
              </div>
            ))
          )}
        </div>

        <div>
          <h2>Map</h2>
          <GoogleMap destinations={getAllDestinations()} />
        </div>
      </div>
    </div>
  );
};

export default Itineraries;
